<?php/** Main Admin functions class which responsible for the entire amdin functionality and scripts loaded and files.**/if ( ! defined( 'ABSPATH' ) ) exit; // Exit if accessed directly/** * WP_Event_Manager_Admin class. */class WP_Event_Manager_Admin {	/**	 * __construct function.	 *	 * @access public	 * @return void	 */	public function __construct() {		include_once( 'wp-event-manager-cpt.php' );		include_once( 'wp-event-manager-settings.php' );		include_once( 'wp-event-manager-writepanels.php' );		include_once( 'wp-event-manager-setup.php' );		$this->settings_page = new WP_Event_Manager_Settings();		add_action( 'admin_menu', array( $this, 'admin_menu' ), 12 );		add_action( 'admin_enqueue_scripts', array( $this, 'admin_enqueue_scripts' ) );	}	/**	 * admin_enqueue_scripts function.	 *	 * @access public	 * @return void	 */	public function admin_enqueue_scripts() {		global $wp_scripts;		$screen = get_current_screen();			//main frontend, bootstrap & bootstrap calendar style 			wp_register_style( 'bootstrap-main-css', EVENT_MANAGER_PLUGIN_URL . '/assets/js/bootstrap/css/bootstrap.less');			wp_register_style( 'bootstrap-datepicker-css', EVENT_MANAGER_PLUGIN_URL.'/assets/js/jquery-timepicker/bootstrap-datepicker.css');		wp_register_style( 'jquery-timepicker-css', EVENT_MANAGER_PLUGIN_URL.'/assets/js/jquery-timepicker/jquery.timepicker.css');		if (!wp_style_is( 'bootstrap.min.css', 'enqueued' )) 		{		    wp_enqueue_style( 'bootstrap-main-css');		}				if (!wp_style_is( 'jquery.timepicker.css', 'enqueued' )) 		{		    wp_enqueue_style( 'jquery-timepicker-css');		}		if (!wp_style_is( 'bootstrap-datepicker.css', 'enqueued' )) 		{		    wp_enqueue_style( 'bootstrap-datepicker-css');		}		wp_enqueue_style( 'event_manager_admin_css', EVENT_MANAGER_PLUGIN_URL . '/assets/css/backend.css' );			//bootstrap, moment and bootstrap calendar js					wp_register_script( 'bootstrap-main-js', EVENT_MANAGER_PLUGIN_URL . '/assets/js/bootstrap/js/bootstrap.min.js', array('jquery'), EVENT_MANAGER_VERSION, true);		wp_register_script( 'jquery-timepicker-js', EVENT_MANAGER_PLUGIN_URL . '/assets/js/jquery-timepicker/jquery.timepicker.min.js',array('jquery'), EVENT_MANAGER_VERSION, true);		wp_register_script( 'bootstrap-datepicker-js', EVENT_MANAGER_PLUGIN_URL . '/assets/js/jquery-timepicker/bootstrap-datepicker.js',array('jquery-timepicker-js'), EVENT_MANAGER_VERSION, true);        if (!wp_script_is( 'bootstrap.min.js', 'enqueued' )) 		{		    wp_enqueue_script( 'bootstrap-main-js');		}						if (!wp_script_is( 'bootstrap-datepicker.js', 'enqueued' )) 		{		   wp_enqueue_script( 'bootstrap-datepicker-js');			}				if ( in_array( $screen->id, apply_filters( 'event_manager_admin_screen_ids', array( 'edit-event_listing', 'event_listing', 'event_listing_page_event-manager-settings', 'event_listing_page_event-manager-addons' ) ) ) ) 		{			$jquery_version = isset( $wp_scripts->registered['jquery-ui-core']->ver ) ? $wp_scripts->registered['jquery-ui-core']->ver : '1.9.2';						wp_enqueue_style( 'jquery-ui-style', '//code.jquery.com/ui/' . $jquery_version . '/themes/smoothness/jquery-ui.css', array(), $jquery_version );						wp_register_script( 'jquery-tiptip', EVENT_MANAGER_PLUGIN_URL. '/assets/js/jquery-tiptip/jquery.tipTip.min.js', array( 'jquery' ), EVENT_MANAGER_VERSION, true );				wp_enqueue_script( 'event_manager_admin_js', EVENT_MANAGER_PLUGIN_URL. '/assets/js/admin.min.js', array( 'jquery', 'jquery-tiptip','bootstrap-datepicker-js'), EVENT_MANAGER_VERSION, true );		}					wp_register_script( 'wp-event-manager-admin-settings', EVENT_MANAGER_PLUGIN_URL. '/assets/js/admin-settings.min.js', array( 'jquery' ), EVENT_MANAGER_VERSION, true );	}	/**	 * admin_menu function.	 *	 * @access public	 * @return void	 */	public function admin_menu() {		add_submenu_page( 'edit.php?post_type=event_listing', __( 'Settings', 'wp-event-manager' ), __( 'Settings', 'wp-event-manager' ), 'manage_options', 'event-manager-settings', array( $this->settings_page, 'output' ) );		if ( apply_filters( 'event_manager_show_addons_page', true ) )			add_submenu_page(  'edit.php?post_type=event_listing', __( 'WP Event Manager Add-ons', 'wp-event-manager' ),  __( 'Add-ons', 'wp-event-manager' ) , 'manage_options', 'event-manager-addons', array( $this, 'addons_page' ) );	}	/**	 * Output addons page	 */	public function addons_page() {		$addons = include( 'wp-event-manager-addons.php' );		$addons->output();	}}new WP_Event_Manager_Admin();